  //   border-radius: 100px;
  border-width: 0px;
  color: ${namedColors.buttonSign};
  font-size: 25px;
  justify-content: center;
  align-items: center;
  display: flex;
  height: 30px;
  width: 30px;
  &:focus {
  }
`;

export const LoginButton = styled.button`
