import { useState } from "react";
import { CategoryFIlters } from "../locales/filters";
import { DeleteImg } from "../locales/images/delete";
import { SearchImg } from "../locales/images/search";
import { StartSearchImg } from "../locales/images/startSearch";
import { Filter } from "../models/Filter";
import { Button, IconButton } from "../styles/Buttons";
import { InfoScreenTextBox } from "../styles/MainScreen";
import {
  Input,
  SearchBarBox,
  SearchHandleBox,
  SearchMainBox,
} from "../styles/Search";
import { filterCatButton } from "./buttons/FilterCatButton";
import { Filters } from "./Filters";
export const Search = ({ filter, setFilter }: any) => {
  const [isHidden, setIsHidden] = useState(false);
  return (
    <SearchMainBox theme={isHidden}>
      {/* <div
        style={{
          backgroundColor: "red",
          width: "100%",
          height: "50%",
          display: "flex",
        }}
      >
        <SearchBarBox>
        
          <StartSearchImg />
          <Filters filter={filter} setFilter={setFilter}></Filters>
        </SearchBarBox>
      </div>

      {CategoryFIlters.map((data, index) => (
        <button>{data.name}</button>
      ))} */}
      <div style={{ display: "flex", width: "100%" }}>
        <Input style={{ flex: 4 }} />
        <div
          style={{
            flex: 1,
            display: "flex",
            alignItems: "center",
            justifyContent: "cenbter",
          }}
        >
          <StartSearchImg />
        </div>
        <Filters filter={filter} setFilter={setFilter}></Filters>
      </div>
      <div> {CategoryFIlters.map((data, index) => filterCatButton(data))}</div>

      <SearchHandleBox onClick={() => setIsHidden(!isHidden)}>
        {isHidden ? <SearchImg /> : <SearchImg />}
      </SearchHandleBox>
    </SearchMainBox>
  );
};
