import { createSlice } from "@reduxjs/toolkit";
import { Food } from "../models/Food";
import { FoodData } from "../testData/FoodData";

export interface CartI {
  cart: Food[];
}

const initialState: CartI = {
  cart: [],
};
export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    clear: (state) => {
      state.cart = [];
    },
    upgradeCart: (state, action) => {
      return (state.cart = [...state.cart, action.payload]);
    },
  },
});

export const { upgradeCart, clear } = cartSlice.actions;

export default function todosReducer(state = initialState, action: any) {
  switch (action.type) {
    case "todos/todoAdded": {
      // Can return just the new todos array - no extra object around it
      return [...state];
    }
    case "todos/todoToggled": {
      return state.map((todo) => {
        if (todo.id !== action.payload) {
          return todo;
        }
      });
    }
    default:
      return state;
  }
}
