import styled from "styled-components";
import { colors, namedColors } from "../locales/colors";

export const Mainbox = styled.div`
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const FoodBox = styled.div`
  max-height: 400px;
  min-height: 200px;
  min-width: 200px;
  max-width: 400px;
  border: 2px solid ${namedColors.foodBorder};
  border-radius: 30px;
  background-color: ${colors.pink};
  margin: 10px;
  display: flex;
  flex-direction: column;
`;

export const FoodPhoto = styled.img`
  height: 220px;
  width: 220px;
  border-radius: 28px 28px 0px 0px;
`;

export const FoodPhotoTitle = styled.h2`
  color: ${colors.white};
`;
