import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";

export const Text = styled.label`
  font-size: 25px;
  color: ${rawColors.blue};
`;

export const MainTitle = styled.label`
  font-size: 35px;
  color: ${rawColors.red};
`;

export const FoodBoxText = styled.section`
  width: 100%;
  overflow: hidden;
  font-family: "PT Serif Caption";
  font-size: 20px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
  text-align: center;
`;

export const TextSerif = styled.section`
  font-family: "PT Serif Caption";
  font-size: 28px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
`;

export const TextOswald = styled.section`
  font-family: Oswald;
  font-size: 37px;
  font-style: normal;
  font-variant: normal;
  font-weight: 700;
  line-height: 26.4px;
  color: red;
  text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
  position: absolute;
  margin-top: 100px;
  margin-left: 6px;
`;

export const TextIrish = styled.section`
  font-family: "Irish Grover";
  font-size: 44px;
  font-style: normal;
  font-variant: normal;
  font-weight: 400;
  line-height: 44px;
`;
