import React, { useRef, useState } from "react";
import { FoodBox, FoodButtonsDiv, FoodPhoto } from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { FoodBoxText, Text, TextSerif } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);
  const ref = useRef();

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  function cutText(text: string, length: number) {
    if (text.length < length) return text;
    else
      return text.substr(0, text[length] === " " ? length : length - 2) + "...";
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto theme={data.imageUrl}>
        <FoodButtonsDiv>
          <FoodPhotoButton icon={"info"} color={colors.buttonColor1} />
          <FoodPhotoButton
            icon={"cart"}
            color={colors.buttonColor1}
            onClick={() => add()}
          />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxText
        ref={(el) => {
          console.log(el?.offsetWidth);
        }}
      >
        {cutText(data.name + "dziaba dziaba", 35)}
      </FoodBoxText>
      <div
        style={{
          flexDirection: "row",
          display: "flex",
          justifyContent: "space-around",
          height: "50%",
        }}
      >
        <TextSerif>{data.price}$</TextSerif>
        <div
          style={{
            flexDirection: "row",
            display: "flex",
            height: "50%",
            marginBottom: "2px",
          }}
        >
          <FoodPhotoButton
            icon={"minus"}
            color={colors.buttonColor1}
            sqr={true}
            onClick={() => (amount > 1 ? setAmount(amount - 1) : null)}
          />
          <TextSerif>{amount}</TextSerif>
          <FoodPhotoButton
            icon={"plus"}
            color={colors.buttonColor1}
            sqr={true}
            onClick={() => setAmount(amount + 1)}
          />
        </div>
      </div>
    </FoodBox>
  );
};
