import { createSlice } from "@reduxjs/toolkit";
import { Food } from "../models/Food";
import { FoodData } from "../testData/FoodData";

export interface FoodI {
  food: Food[];
}

const initialState: FoodI = {
  food: FoodData,
};
export const foodSlice = createSlice({
  name: "food",
  initialState,
  reducers: {
    incrementAmount: (state, action) => {
      const xxx = state.food.find((x) => x.id === action.payload);
      xxx[0] ? (xxx[0].amount += 1) : null;
    },
    decrementAmount: (state, action) => {
      state.food[action.payload.id].amount -= 1;
    },
    incrementByAmount: (state, action) => {
      state.food[1].amount += action.payload;
    },
  },
});

export const { incrementAmount, decrementAmount } = foodSlice.actions;

export default foodSlice.reducer;
