import { useState, useEffect } from "react";
import { Food } from "../../models/Food";
import { FoodTile } from "../../models/FoodComp";
import { FlexPart, GridBox } from "../../style";
import { CartBox } from "../../styles/CartBox";
import { GridMyListBox, ListHandleBox } from "../../styles/MyLists";
import { CartSlider } from "../CartSlider";
import { generateList } from "../functions/itemList";

import { moveCartSlider } from "../functions/moveCartSlider";
import { MyListsElementBox } from "../MyListsElementBox";

export const MyListsSub = ({ data }: any) => {
  const [cart, setCart] = useState<any>(data ? data.cart : []);
  const [windowSize, setWindowSize] = useState(getWindowSize());
  const [popup, setPopup] = useState({
    isActive: false,
    message: "",
    type: "",
  });
  function getWindowSize() {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  }
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart?.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  const [active, setActive] = useState<boolean>(false);

  return (
    <>
      <GridMyListBox
        theme={{
          width: windowSize.innerWidth,
        }}
      >
        {popupBlock({
          popup,
          setPopup,
        })}
        <MyListsElementBox data={cart} setData={setCart} />
      </GridMyListBox>
      <CartBox
        theme={{
          img: require("../../locales/images/foodBg1.png"),
          width: windowSize.innerWidth,
        }}
      ></CartBox>
    </>
    // <div style={{ width: "100%", height: "100%" }}>
    //   {cart.map((data, index) => (
    //     <ListHandleBox onClick={() => setActive(!active)} theme={active}>
    //       {active ? generateList(data, setCart, "mode") : null}
    //     </ListHandleBox>
    //   ))}
    // </div>
  );
};
