import { Food } from "../../models/Food";
import { SerifText } from "../../styles/TextStyles";
import { CartFood } from "../CartFood";
import { priceAll } from "./priceAll";

export function generateList({ cart, setCart, mode }: any) {
  let clone = [...cart];
  let count = clone.filter((data) => data.id === clone[clone.length - 1].id);
  let qqq = clone.findIndex((data1) => data1.id === clone[clone.length - 1].id);

  if (count.length >= 2) {
    clone[qqq].amount = clone[qqq].amount + clone[cart.length - 1].amount;
    cart.pop();
  }
  return (
    <>
      {clone.map((element: Food, index: number) => (
        <CartFood
          data={element}
          cart={clone}
          setCart={setCart}
          index={index}
          number={element.amount}
          mode={mode}
        />
      ))}
      {mode === "cart" ? null : (
        <SerifText style={{ margin: "5%" }}>Suma: {priceAll(cart)}$</SerifText>
      )}
    </>
  );
}
