import { flexbox } from "@mui/system";
import { GrayButton, GrayButtonPopup } from "../styles/Buttons";
import { inputDataSpecial, inputDataSpecialPopup } from "../styles/Constants";
import {
  PopupWindowMainBox,
  PopupWindowBox,
  GrayButtonInput,
} from "../styles/Input";
import { FormSpecialBox } from "../styles/OrderTrans";
import { setSingleInput, setInputs } from "./functions/setInputsToForm";

export function popupBlock(
  options: any,
  handle: any,
  register: any,
  setOptions: any
) {
  if (options.isActive === true) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "scroll";
      // html.style.position = "fixed";
    }
    return (
      <PopupWindowMainBox>
        <PopupWindowBox>
          <form onSubmit={handle}>
            {setSingleInput(
              inputDataSpecialPopup.data[
                options.variant === "deliveryTime" ? 0 : 1
              ],
              register,
              ""
            )}
          </form>
          <GrayButtonPopup
            onClick={() => setOptions({ isActive: false, variant: "" })}
          >
            Choose
          </GrayButtonPopup>
        </PopupWindowBox>
      </PopupWindowMainBox>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "scroll";
      html.style.position = "static";
    }
  }
}
