import { createSlice } from "@reduxjs/toolkit";

export interface SettingsI {
  loginData: {
    login: string;
    password: string;
  };
  language: string;
}

const initialState: SettingsI = {
  loginData: {
    login: "",
    password: "",
  },
  language: "eng",
  darkMode: false,
};
export const settingsSlice = createSlice({
  name: "settings",
  initialState,
  reducers: {
    setDarkMode: (state) => {
      state.darkMode = !state.darkMode;
    },
    setLanguage: (state) => {},
    setLogged: (state, action) => {},
  },
});

export const { setDarkMode, setLanguage, setLogged } = settingsSlice.actions;

export default settingsSlice.reducer;
